import React, { useState, useMemo, useCallback } from "react";

// React.memo ile gereksiz render'ı önleyen bileşen
const MemoizedButton = React.memo(({ onClick, children }) => {
  console.log("Button Render Oldu!");
  return <button onClick={onClick}>{children}</button>;
});

const App = () => {
  const [count, setCount] = useState(0);
  const [darkMode, setDarkMode] = useState(false);

  // useMemo: Ağır hesaplamayı optimize etme
  const computedValue = useMemo(() => {
    console.log("Ağır hesaplama yapılıyor...");
    return count * 2;
  }, [count]);

  // useCallback: Butonun onClick fonksiyonunu optimize etme
  const increment = useCallback(() => {
    setCount((prev) => prev + 1);
  }, []);

  return (
    <div style={{ background: darkMode ? "#333" : "#fff", color: darkMode ? "#fff" : "#000", padding: "20px" }}>
      <h2>Counter: {count}</h2>
      <h3>Hesaplanan Değer: {computedValue}</h3>

      {/* useCallback ile sarmalanan buton */}
      <MemoizedButton onClick={increment}>Artır</MemoizedButton>

      {/* Tema değiştirme butonu */}
      <button onClick={() => setDarkMode((prev) => !prev)}>
        Tema Değiştir ({darkMode ? "Açık" : "Koyu"})
      </button>
    </div>
  );
};

export default App;
